// src/components/Navigation.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { loadNavigationConfig, getStaticNavigationConfig } from '../utils/ymlLoader';
import './Navigation.css';

function Navigation() {
  const [navigationItems, setNavigationItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Try to load the navigation config from the YAML file
    const loadNavigation = async () => {
      try {
        // First attempt to load the navigation dynamically
        const navItems = await loadNavigationConfig();

        if (navItems && Array.isArray(navItems) && navItems.length > 0) {
          setNavigationItems(navItems);
        } else {
          // Fall back to static config if dynamic loading fails
          console.log('Using static navigation config as fallback');
          setNavigationItems(getStaticNavigationConfig());
        }
      } catch (error) {
        console.error('Navigation loading error:', error);
        setError(`Error loading navigation: ${error.message}`);
        // Still use the static config as fallback
        setNavigationItems(getStaticNavigationConfig());
      } finally {
        setIsLoading(false);
      }
    };

    loadNavigation();
  }, []);

  if (isLoading) return <div className="navigation-loading">Loading navigation...</div>;
  if (error) return <div className="navigation-error">{error}</div>;

  return (
    <nav className="main-navigation">
      <ul className="nav-list">
        {navigationItems.map((item, index) => (
          <li key={index} className="nav-item">
            {item.url ? (
              <Link to={item.url} className="nav-link">{item.title}</Link>
            ) : (
              <span className="nav-category">{item.title}</span>
            )}

            {/* Handle both "links" (our format) and "children" (Jekyll format) */}
            {((item.links && item.links.length > 0) || (item.children && item.children.length > 0)) && (
              <ul className="nav-sublist">
                {(item.links || item.children || []).map((link, linkIndex) => (
                  <li key={linkIndex} className="nav-subitem">
                    <Link to={link.url} className="nav-sublink">{link.title}</Link>
                  </li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </nav>
  );
}

export default Navigation;
