export function getApiUrl() {
  const isLocalhost = window.location.hostname === 'localhost' ||
                     window.location.hostname === '127.0.0.1';

  return isLocalhost
    ? 'http://localhost:8080/.netlify/v1/functions'  // 添加了 /v1 以匹配文件结构
    : '/.netlify/v1/functions';  // 生产环境也需要修改
}
